import React from 'react';
import axios from 'axios';
import '../../App.css';
import { purple } from '@material-ui/core/colors';

export default class Posts extends React.Component {
  state = {
    posts: []
  }

  componentDidMount() {
    axios.get(`http://127.0.0.1:8000/api/posts`)
      .then(res => {
        const posts = res.data;
        this.setState({ posts });
      })
  }

  render() 
  {
    return(
      <>
      <h1>Posts</h1>
      <div>
        {
          this.state.posts.map((posts, index) =>{
            return (
              <div classname="box--container" style={{marginRight:'20px', background:'pink'}}>
                <div class="author" key={index}>{posts.author}</div>
                <div class="rating" key={index}>{posts.rating}</div>
                <div class="categories" key={index}>{posts.categories.value}</div>
                <div class="title" key={index}>{posts.title}</div>
                <div class="content" key={index}>{posts.content}</div>
              </div>
            );
          })
        }
      </div>
      </>
    );
  }
}
