import React from 'react';
import axios from 'axios';
import '../../App.css';
import { Link } from 'react-router-dom';

export default class Posts extends React.Component {
  constructor (props) {
    super(props);
    this.state = {
      post: [],
      user: [],
      postId: props.match.params.id
    }
  }

  async componentDidMount() {
    const res = await axios.get(`http://127.0.0.1:8000/api/posts`)
      const posts = res.data;
      this.setState({ posts });

    axios.get(`http://127.0.0.1:8000/api/users/` + this.state.posts.author)
    .then(res => {
      const user = res.data;
      this.setState({ user });
    })
  }

  render() 
  {
    console.log(this.state);
    return(
      <>
      <div class="box--container-wrapper">
        <h1>Posts<button><Link to="/createPost">Make a new post</Link></button></h1>
        <div class="box--container">
        {
          this.state.posts.map((mapPosts, ind) =>{
            const url = "/posts/" + mapPosts.id
            return (
              <div class={"boxes"}>

                <div class="author" key={ind}>Author: {this.state.user.name}</div>
                <div class="title" key={ind}>Title: {mapPosts.title}</div>
                <div class="content" key={ind}>Content: {mapPosts.content}</div>
                <div class="rating" key={ind}>Rating: {mapPosts.rating}</div>
                <span><Link to={url}>Go to post</Link></span>
              </div>
            );
          })
        }
          </div>
        </div>
      </>
    );
  }
}
