import React from 'react';
import axios from 'axios';
import '../../App.css';
import './Users.css';

export default class Profile extends React.Component {
  state = {
    user: []
  }

  constructor(props) {
    super(props);
    props.match.params.id
    // this.state = {
    //   id: props.match.params.id
    // }
  }

  componentDidMount() {
    axios.get(`http://127.0.0.1:8000/api/users/` + props.match.params.id)
      .then(res => {
        const user = res.data;
        this.setState({ user });
      })
  }

  render() 
  {
    return(
      <>
      <div class="box--container-wrapper">
        <h1>Profile </h1>

        <div class="box--container">
        {
          this.state.user.map((person, index) =>{
            return (
              <div class={"boxes"}>
                <div class="login" key={index}>Login: {person.login}</div>
                <div class="name" key={index}>Name: {person.name}</div>
                <div class="rating" key={index}>Rating: {person.rating}</div>
                <div class="avatar" key={index}> 
                  <img  class="avatarpic" src={'http://localhost:8000/' + person.avatar} alt="Image not found" />
                </div>
              </div>
            );
          })
        }
        </div>
      </div>
      </>
    );
  }
}
