import React from 'react';
import axios from 'axios';
import '../../App.css';

export default class Posts extends React.Component {
  state = {
    posts: []
  }

  componentDidMount() {
    axios.get(`http://127.0.0.1:8000/api/posts`)
      .then(res => {
        const posts = res.data;
        this.setState({ posts });
      })
  }

  render() 
  {
    return(
      <div style={{display:"flex", flexDirection:'row'}}>
        {
          this.state.users.map((person, ind) =>{
            return(
              <div style={{marginRight:'20px'}}>
                <div class="author" key={ind}>{person.author}</div>
                <div class="rating" key={ind}>{person.rating}</div>
                <div class="categories" key={ind}>{person.categories}</div>
                <div class="title" key={ind}>{person.title}</div>
                <div class="content" key={ind}>{person.content}</div>
              </div>
            );
          })
        }
      </div>
    );
  }
}
