import React from 'react';
import axios from 'axios';
import '../../App.css';
import { Link } from 'react-router-dom';

export default class Posts extends React.Component {
  state = {
    posts: [],
    user: []
  }

  async componentDidMount() {
    const res = await axios.get(`http://127.0.0.1:8000/api/posts`)
      // .then(res => {
        const posts = res.data;
        this.setState({ posts });
      // })

      axios.get(`http://127.0.0.1:8000/api/users/` + this.state.posts.author)
      .then(res => {
        const user = res.data;
        this.setState({ user });
      })
  }

  render() 
  {
    return(
      <>
      <div class="box--container-wrapper">
        <h1>Posts<button><Link to="/createPost">Make a new post</Link></button></h1>
        <div class="box--container">
        {
          this.state.posts.map((post, ind) =>{
            const url = "/posts/" + post.id
            return (
              <div class={"boxes"}>
                <div class="rating" key={ind}>Author: {post.rating}</div>
                <div class="login" key={ind}>Title: {post.login}</div>
                <div class="name" key={ind}>Content: {post.name}</div>
                <div class="rating" key={ind}>Rating: {post.rating}</div>
                <span><Link to={url}>Go to post</Link></span>
              </div>
            );
          })
        }
          </div>
        </div>
      </>
    );
  }
}
