        console.log(res.data);
      })
  }

  render() {
    return (
      <div class="registration--wrapper">
        <form class="registration--form" onSubmit={this.handleSubmit}>
          Your login:
          <input placeholder="Enter your login..." type="text" name="login" onChange={this.handleChange} />
          Your password:
          <input placeholder="Enter your passoword..." type="text" name="password" onChange={this.handleChange} />
          Confirm your password:
          <input placeholder="Confirm your password..." type="text" name="confirm_password" onChange={this.handleChange} />
          Your e-mail:
          <input placeholder="Enter your email..." type="text" name="email" onChange={this.handleChange} />
          <button type="submit">Register</button>
        </form>
      </div>
    )
  }
}
