import React, { useState } from 'react'
import toast, { Toaster } from 'react-hot-toast'
import { Link, useParams, useHistory } from 'react-router-dom'
import { Helmet } from 'react-helmet'
import Cookies from 'js-cookie'
import axios from 'axios'
import '../../../App.css';

export function Login() {
    const [login, setloginlogin] = useState('')
    const [password, setPassword] = useState('')

    const handleChange = e => {
        switch(e.target.name){
            case 'login': setloginlogin(e.target.value); break
            case 'password': setPassword(e.target.value); break
        }
    }

    const handleSubmit = () => {
        const api = {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            data: {
               login:login,
                password: password
            },
            url: "https://orbimind.herokuapp.com/api/auth/login"
        };
        const promise = axios.post(api.url, api.data, { headers: api.headers });

        toast.promise(
            promise, 
            {
                loading: 'Logging in...',
                success: (response) => {
                    Cookies.set('user', { 
                        id: response.data.user.id,
                       login: response.data.user.loginlogin,
                        name: response.data.user.name,
                        email: response.data.user.email,
                        image: response.data.user.image,
                        token: response.data.token,
                    }, { 
                        expires: (response.data.expires_in / 86400),
                        sameSite: 'strict'
                    });
                    setTimeout(() => location.replace('/posts'), 1500);
                    return response.data.message;
                },
                error: (error) => {
                    if(error.response.data.errors) {
                        if(error.response.data.errors.loginlogin)
                            for(let i = 0; i < error.response.data.errors.loginlogin.length; i++)
                                toast.error(error.response.data.errors.loginlogin[i])
                        if(error.response.data.errors.password)
                            for(let i = 0; i < error.response.data.errors.password.length; i++)
                                toast.error(error.response.data.errors.password[i])
                    }
                    return error.response.data.message;
                }
            }
        );
    }

    return (
        <div className="authScreen unselectable">
            <Helmet>
                <title>Login</title>
            </Helmet>
            <Logo className='popTop' />
            <h1 className='fadeIn'>Welcome back!</h1>
