import axios from 'axios';
import '../../App.css';
import './Users.css';

export default class Profile extends React.Component {
  state = {
    id: [],
    user: []
  }

  constructor(props) {
    super(props);
    this.id = props.match.params.id;
    // this.state = {
    //   id: props.match.params.id
    // }
  }

  componentDidMount() {

    axios.get(`http://127.0.0.1:8000/api/users/` + this.id)
      .then(res => {
        const user = res.data;
        console.log(user)
        // this.setState({ user });
      })
  }

  render() 
  {
    return(
      <>
      <p>Profile</p>
        <div class="login">Login: {user.login}</div>
      </>
    );
  }
}
