import React from 'react';
import axios from 'axios';
import '../../App.css';

export function Posts() {
  const [response, setResponse] = useState(null)

  useEffect(async() => {
    const response = await axios.get("http://127.0.0.1:8000/api/posts")
    const {data} = response
    setResponse(response)
  }, [])
  console.log(response?.data);

  return (
    <>
    <h1>Posts</h1>
    <div className="posts--card"> 
      <div>{response?.data[0].author}</div>
      <div>{response?.data[0].title}</div>
      <div>{response?.data[0].status}</div>
      <div>{response?.data[0].rating}</div>
      <div>{response?.data[0].description}</div>

      <div>{response?.data?.map(v => v.categories.value+" ")}</div>
      <div>{response?.data[0].locked}</div>
    </div>
    </>
  );
}



export default class PersonList extends React.Component {
  state = {
    users: []
  }

  componentDidMount() {
    axios.get(`http://127.0.0.1:8000/api/users`)
      .then(res => {
        const users = res.data;
        this.setState({ users });
      })
  }

  render() 
  {
    return(
      <div style={{display:"flex", flexDirection:'row'}}>
        {
          this.state.users.map((person, ind) =>{
            return(
              <div style={{marginRight:'20px'}}>
                <div class="login" key={ind}>{person.login}</div>
                <div class="name" key={ind}>{person.name}</div>
                <div class="rating" key={ind}>{person.rating}</div>
                <div class="avatar" key={ind}>{person.avatar}</div>
              </div>
            );
          })
        }
      </div>
    );
  }
}
