import React from 'react';
import axios from 'axios';
import '../../App.css';
import { Link } from 'react-router-dom';

export default class Post extends React.Component {
  state = {
    post: [],
    user: []
  }

  componentDidMount() {
    axios.get(`http://127.0.0.1:8000/api/posts` + this.post.id)
      .then(res => {
        const post = res.data;
        this.setState({ post });
      })
  }

  componentDidMount() {
    axios.get(`http://127.0.0.1:8000/api/users` + this.post.author)
      .then(res => {
        const user = res.data;
        this.setState({ user });
        console.log(res.data);
      })
  }

  render() 
  {
    return(
      <>
      <div class="box--container-wrapper">
        <p>Post 1</p>
        <div class="box--container">
              return (
                  <div class={"boxes"}>
                    <div class="author">Author: post.author</div>
                    <div class="rating">Rating: post.rating</div>
                    <div class="categories">Categories: post.categories.value</div>
                    <div class="title">Title: post.title</div>
                    <div class="content">Content: post.content</div>
                  </div>
              );
          </div>
        </div>
      </>
    );
  }
}
